document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".header__burger"),t=document.querySelector(".header__menu");e.addEventListener("click",()=>{e.classList.toggle("active"),t.classList.toggle("active"),document.body.classList.toggle("lock")}),window.addEventListener("resize",()=>{e.classList.remove("active"),t.classList.remove("active"),document.body.classList.remove("lock")})});